sys_variable_value.value:
(function execute(inputs, outputs) {
  var ismatch=false;
  var dateMonth=inputs.dateMonth.split('-');
  var recDate=dateMonth[0];
  var recMonth=dateMonth[1];
  var gd =new GlideDate();
  var currentDate=gd.getDayOfMonthNoTZ();
  var currentMonth=gd.getMonthNoTZ();  
  gs.info(' Output is '+  recDate + '  '+recMonth +'  '+currentDate+'   '+currentMonth+' ');
  if(recDate==currentDate && recMonth==currentMonth)
    outputs.ismatch = true;
    //gs.info('success');
})(inputs, outputs);

